import { createStore } from 'vuex'

export const store = createStore({
    state: {
        backendUrl: 'http://127.0.0.1:8000/api/cp1',
        auth: false,
    },
    mutations: {
      setAuth: (state, payload) => {
        state.auth = payload;
      },
    },
    actions: {
      setAuth: (context, payload) => {
        context.commit('setAuth', payload);
      },
    },
    modules: {},
    getters: {
        getServerUrl: state => {
            return state.backendUrl
        },
        getAuth: state => {
            return state.auth
        }
    }
})
